export const TechStack = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: "-100px" });

  return (
    <section id="tech" className="bg-white py-24 overflow-hidden">
      <div className="mx-auto px-6 max-w-7xl">
        <motion.h2
          initial={{ opacity: 0, y: 30 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          className="mb-4 font-serif text-black text-3xl md:text-5xl md:text-center text-start"
          style={{ fontFamily: "Playfair Display, serif" }}
        >
          Tech Stack
        </motion.h2>
        <motion.div
          initial={{ opacity: 0, scaleX: 0 }}
          animate={isInView ? { opacity: 1, scaleX: 1 } : {}}
          transition={{ delay: 0.2, duration: 0.6 }}
          className="bg-[#D4AF37] md:mx-auto mb-16 w-20 h-1"
        ></motion.div>

        <div ref={ref} className="gap-8 grid md:grid-cols-2 lg:grid-cols-3">
          {techCategories.map((category, categoryIndex) => {
            const IconComponent = category.icon;
            return (
              <motion.div
                key={category.title}
                initial={{ opacity: 0, y: 50 }}
                animate={isInView ? { opacity: 1, y: 0 } : {}}
                transition={{ delay: categoryIndex * 0.1, duration: 0.6 }}
                className="bg-gradient-to-br from-gray-50 to-white shadow-lg hover:shadow-xl p-6 border border-gray-200 hover:border-[#D4AF37] rounded-2xl transition-all"
              >
                <div className="flex items-center gap-3 mb-6">
                  <div className="flex justify-center items-center bg-[#D4AF37] rounded-lg w-10 h-10">
                    <IconComponent className="text-white" size={20} />
                  </div>
                  <h3 className="font-bold text-black text-sm md:text-lg">
                    {category.title}
                  </h3>
                </div>

                <div className="space-y-3">
                  {category.items.map((tech, techIndex) => (
                    <motion.div
                      key={tech.name}
                      initial={{ opacity: 0, x: -20 }}
                      animate={isInView ? { opacity: 1, x: 0 } : {}}
                      transition={{
                        delay: categoryIndex * 0.1 + techIndex * 0.05,
                        duration: 0.4,
                      }}
                      whileHover={{ scale: 1.03, x: 5 }}
                      className="flex items-center gap-3 bg-white hover:bg-gray-50 shadow-sm px-4 py-3 rounded-lg transition-all cursor-pointer"
                    >
                      <span className="text-2xl">{tech.icon}</span>
                      <span className="font-medium text-gray-800">
                        {tech.name}
                      </span>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            );
          })}
        </div>
      </div>
    </section>
  );
};
